 document.addEventListener("DOMContentLoaded",function(){ class R{constructor(S){this.S=S;}r_d(){this.S^=this.S<<13;this.S^=this.S>>17;this.S^=this.S<<5;return((this.S<0?~this.S+1:this.S)%1000)/1000;}r_b(a,b){return a+(b-a)*this.r_d();}r_i(a,b){return Math.floor(this.r_b(a,b+1));}r_c(x){return x[Math.floor(this.r_b(0,x.length*0.99))];}};const S=4294967295;const r=new R(S);const ns="http://www.w3.org/2000/svg";const dX=r.r_i(-5,5);const dY=r.r_i(-5,5);const rF=r.r_b(1,5);const cF=r.r_b(5,15);const ra=r.r_c([30,60,90,120,150.180]);let s=1000;let l=r.r_b(1,360);let lR=(l+180)%360;const bg=document.createElementNS(ns,"rect");bg.setAttribute("width",s);bg.setAttribute("height",s);bg.setAttribute("fill","hsl("+lR+",100%,50%)");document.getElementById("JSVG").appendChild(bg);let rt=[];for(let i=0;i<200;i++){ let mR=document.createElementNS(ns,"rect");mR.setAttribute("x",r.r_b(0,s));mR.setAttribute("y",r.r_b(0,s));mR.setAttribute("width",r.r_b(1,s/rF));mR.setAttribute("height",r.r_b(1,s/rF));mR.setAttribute("fill","hsl("+((r.r_b(0,1)*(lR+ra-(lR-ra)))+(lR-ra))+","+((r.r_b(70,100)))+"%"+","+((r.r_b(50,80)))+"%");mR.setAttribute("fill-opacity",(r.r_b(.8,1)));mR.setAttributeNS(null,"clip-path","url(#clip)");document.getElementById("JSVG").appendChild(mR);rt.push(mR);};let cr=[];for(let i=0;i<200;i++){ let mC=document.createElementNS(ns,"circle");mC.setAttribute("cx",r.r_b(1,1000));mC.setAttribute("cy",r.r_b(1,1000));mC.setAttribute("r",r.r_b(2,cF));mC.setAttribute("fill","hsl("+((r.r_b(0,1)*(l+ra-(l-ra)))+(l-ra))+","+((r.r_b(80,100)))+"%"+","+((r.r_b(50,100)))+"%");mC.setAttribute("fill-opacity",(r.r_b(.8,1)));mC.setAttributeNS(null,"clip-path","url(#clip)");document.getElementById("JSVG").appendChild(mC);cr.push(mC);};let clippath=document.createElementNS(ns,"clipPath");clippath.setAttributeNS(null,"id","clip");document.getElementById("JSVG").appendChild(clippath);let rect=document.createElementNS(ns,"rect");rect.setAttributeNS(null,"x","0");rect.setAttributeNS(null,"y","0");rect.setAttributeNS(null,"width","1000");rect.setAttributeNS(null,"height","1000");clippath.appendChild(rect);function jR(){ for(let i=0;i<rt.length;i++){let mR=rt[i];let x=parseInt(mR.getAttribute("x"));let y=parseInt(mR.getAttribute("y"));if(x<-mR.getAttribute("width")){x=1000;}else if(x>1000){x=-mR.getAttribute("width");}if(y<-mR.getAttribute("height")){y=1000;}else if(y>1000){y=-mR.getAttribute("height");}mR.setAttribute("x",x+(r.r_b(0,2))*dX);mR.setAttribute("y",y+(r.r_b(0,2))*dY);}}function jC(){ for(let i=0;i<cr.length;i++){let mC=cr[i];let x=parseInt(mC.getAttribute("cx"));let y=parseInt(mC.getAttribute("cy"));if(x<-mC.getAttribute("width")*3){x=1000;}else if(x>1000){x=-mC.getAttribute("width");}if(y<-mC.getAttribute("height")*2){y=1000;}else if(y>1000){y=-mC.getAttribute("height");}mC.setAttribute("cx",x+(r.r_b(0,2))*dX);mC.setAttribute("cy",y+(r.r_b(0,2))*dY);}}setInterval(jR,r.r_i(25, 75));setInterval(jC,r.r_i(25, 75));}); 
